mapper.update:
UPDATE users


        users_img = #{users_img}


        users_nickname = #{users_nickname}


        users_password = #{users_password}


        users_gender = #{users_gender}


        users_ctnt = #{users_ctnt}


WHERE users_id = #{users_id}

mapper.select:
SELECT contents_id, feed_id, contents_img
FROM contents
WHERE feed_id = ${feed_id}

SELECT A.cmt_id, A.feed_id, A.users_id, A.cmt_cmt, A.cmt_regdt , B.users_img, B.users_nickname
, CASE WHEN COUNT(C.users_id) IS NULL THEN 0 END AS cmtFavCnt , CASE WHEN D.users_id IS NULL THEN 0 ELSE 1 END AS isCmtFav
FROM cmt A LEFT JOIN users B ON A.users_id = B.users_id
LEFT JOIN cmt_fav C ON C.cmt_id = A.cmt_id AND C.feed_id = A.feed_id LEFT JOIN cmt_fav D ON D.cmt_id = A.cmt_id AND D.feed_id = A.feed_id AND D.users_id = ${user4FavCmt}
WHERE A.feed_id = ${feed_id} ORDER BY A.feed_id DESC LIMIT ${startIdx} , ${limit}

mapper.update:
UPDATE users
SET users_img = #{users_img}
WHERE users_id = #{users_id}

mapper.delete:
DELETE FROM follow
WHERE follow_hisFollower = ${follow_hisFollower}
  AND  follow_him = ${follow_him}
