mapper.insert:
INSERT INTO users
(
    users_email, users_name, users_gender,
    users_date_birth, users_nickname, users_password
)
VALUES
(
    #{users_email}, #{users_name}, #{users_gender},
    #{users_date_birth}, #{users_nickname}, #{users_password}
)

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

mapper.select:
SELECT * FROM users
WHERE users_email = #{users_email}
-- AND authCd IS NULL
