mapper.insert:
INSERT INTO users
(
    users_email, users_name, users_gender, users_auth_code,
    users_date_birth, users_nickname, users_password
)
VALUES
(
    #{users_email}, #{users_name}, #{users_gender}, #{users_auth_code},
    #{users_date_birth}, #{users_nickname}, #{users_password}
)

mapper.update:
UPDATE users
SET users_auth_code = null
WHERE users_email = #{users_email}
  AND users_auth_code = #{users_auth_code}
  AND users_is_quit = 0
